const PostLager = ({ articles, loading, time }) => {
    const { start, end } = time;

    if (articles.feed && !loading) {
        if (start != null && end != null) {
            const filterArticles = articles.feed.entries.filter((article) => {
                return (
                    new Date(article.pubDate).getTime() > new Date(start).getTime() &&
                    new Date(article.pubDate).getTime() < new Date(end).getTime()
                );
            });

            return filterArticles.length > 0 ? (
                <div>
                    {filterArticles.map((article, index) => (
                        <div key={index} className="post-preview clearfix">
                         <div data-mh="post-preview-group" className="thumbnail jpibfi_container" style={{ height: '204px' }}>
                             <a href={article.link}><input className="jpibfi" type="hidden" /><img width={320} height={320} src="https://www.eatyourselfskinny.com/wp-content/uploads/2020/10/italian-sliders-5-320x320.jpg" className="attachment-thumbnail size-thumbnail wp-post-image" alt="" loading="lazy" srcSet="https://www.eatyourselfskinny.com/wp-content/uploads/2020/10/italian-sliders-5-320x320.jpg 320w, https://www.eatyourselfskinny.com/wp-content/uploads/2020/10/italian-sliders-5-scaled-225x225.jpg 225w" sizes="(max-width: 320px) 100vw, 320px" data-jpibfi-post-excerpt data-jpibfi-post-url={article.link} data-jpibfi-post-title="Hot Italian Sliders" data-jpibfi-src="https://www.eatyourselfskinny.com/wp-content/uploads/2020/10/italian-sliders-5-320x320.jpg" /></a>
                         </div>
                         <div data-mh="post-preview-group" className="info" style={{ height: '169px' }}>
                             <p className="post-meta">{article.pubDate} <span className="sep">•</span> <a href="https://www.eatyourselfskinny.com/hot-italian-sliders/#respond">0 Comments</a></p>
                             <h2><a href={article.link} rel="bookmark" title="Permanent Link to Hot Italian Sliders">{article.title}</a></h2>
                             <div className="excerpt jpibfi_container">
                                 <input className="jpibfi" type="hidden" /><p>This is a sponsored post written by me on behalf of Safeway. All opinions are 100% mine. Delicious Hot Italian Sliders perfect for game day that are layered with slices of ham, salami, pepperoni and provolone with a homemade roasted red pepper spread! Sundays in our household are no joke this time of year. We […]</p>
                                 <a className="read-more" href={article.link}>READ MORE</a>
                                 <ul className="icons">
                                 </ul>
                             </div>
                         </div>
                        //                     </div>
                    ))}
                </div>
            ) : (
                <div>No articles in the times</div>
            );
        } else {
            return (
                <div>
                    {articles.feed.entries.map((article, index) => (
                        <div key={index} className="item mb-5">
                            <h4>{article.title}</h4>
                            <p>{article.pubDate}</p>
                            <div>
                                {article.content}
                            </div>
                            <a href={article.link} target="_blank" rel="noreferrer">
                                More
                            </a>
                        </div>
                    ))}
                </div>
            );
        }
    } else {
        return <div>Loading...</div>;
    }
};

export default PostLager;
