import React, { Component } from "react";

class SetTime extends Component {
    state = {
        start: null,
        end: null,
        fields: {},
        errors: {}
    };


    handleValidation() {
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        //Name
        if (!fields["time"]) {
            formIsValid = false;
            errors["time"] = "Cannot be empty";
        }
    }

    handleSubmit = (e) => {
        e.preventDefault();
        if (this.handleValidation()) {
            this.props.handleTime(this.state);
        } else {
            alert("Form has errors");
        }
    };

    handleChange = ( field,e) => {
        this.setState({
            [e.target.id]: e.target.value,
        });
        let fields = this.state.fields;
        fields[field] = e.target.value;
        this.setState({ fields });
    };

    render() {
        return (
            <div>
                <form action="" id="form" onSubmit={this.handleSubmit.bind(this)}>
                    <div className="form-item mb-3">
                        <label htmlFor="start">Start</label>
                        <input
                            type="date"
                            id="start"
                            className="form-control"
                            onChange={this.handleChange.bind(this, "name")}
                            value={this.state.fields["time"]}
                        />
                        <span className="error">{this.state.errors["time"]}</span>
                    </div>
                    <div className="form-item mb-3">
                        <label htmlFor="end">End</label>
                        <input
                            type="date"
                            id="end"
                            className="form-control"
                            onChange={this.handleChange.bind(this, "name")}
                            value={this.state.fields["time"]}
                        />
                        <span className="error">{this.state.errors["time"]}</span>
                    </div>
                    <div className="form-item">
                        <button className="btn btn-primary" type="submit">
                            Submit
                        </button>
                    </div>
                </form>
            </div>
        );
    }
}

export default SetTime;
