import React, { Component } from 'react';

class PostLager extends Component {
    constructor() {
        super();
        this.state = {
            recentBlogPost: {
                name: '',
                url: ''
            }
        }
    }

    FetchDataFromRssFeed() {
        var request = new XMLHttpRequest();
        request.onreadystatechange = () => {
            if (request.readyState === 4 && request.status === 200) {
                var myObj = JSON.parse(request.responseText);
                console.log(myObj)
                for (var i = 0; i < 1; i++) {
                    this.setState({
                        recentBlogPost: {
                            name: myObj.items[i].title,
                            url: myObj.items[i].link,
                            imgUrl: myObj.items[i].description,
                            pubDate: myObj.items
                        }
                    });
                }
            }
        }
        request.open("GET", "https://vnexpress.net/rss/suc-khoe.rss", true);
        request.send();
    }

    componentDidMount() {
        { this.FetchDataFromRssFeed() }
    }

    render() {
        return (
            <div>
                Check out our blog: <a target="_blank" href={this.state.recentBlogPost.url}>{this.state.recentBlogPost.name}</a>
